import React from 'react';
import Accordion from 'react-bootstrap/Accordion';
import ScatterPlot from './Plot.js';

import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const chartData = [
  {"elicitation" : "heed", "f1" : 310, "f2" : 1933, "dataset" : "reference"},
  {"elicitation" : "hard", "f1" : 667, "f2" : 1056, "dataset" : "reference"},
  {"elicitation" : "hood", "f1" : 444, "f2" : 880, "dataset" : "reference"},
]

const elicitations = [
  "heed", "hid", "head", "haired", "had", "who'd", "heard", "hud", "hard",
  "hood", "hoard", "hoyed", "hide", "hayed", "hoed", "how'd"
]

function reshapeUserData(userFormantValues) {
  let userChartData = [];
  for (let elicitation in userFormantValues) {
    if (userFormantValues[elicitation]["f1"] && userFormantValues[elicitation]["f2"]) {
      userChartData.push({
        "elicitation" : elicitation,
        "f1" : parseFloat(userFormantValues[elicitation]["f1"]),
        "f2" : parseFloat(userFormantValues[elicitation]["f2"]),
        "dataset" : "user input"
      });
    }
  }
  return userChartData
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      formantValues: {
        "heed" : {
          "f1" : '',
          "f2" : ''
        },
        "hard" : {
          "f1" : '',
          "f2" : ''
        },
        "hoard" : {
          "f1" : '',
          "f2" : ''
        }
      }
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(elicitation, formant, value) {
    // update formant values
    let updateObj = this.state.formantValues;
    updateObj[elicitation][formant] = value;
    this.setState({formantValues : updateObj});
    // add any
  }

  render() {
    return (
      <div className="App">
        <div id="visualisation-pane">
          <div id="visualisation-pane-content">
            <ScatterPlot data={chartData.concat(reshapeUserData(this.state.formantValues))} />
          </div>
        </div>
        <div>
          <FormantForm changeHandler={(e, f, v) => this.handleChange(e, f, v)}/>
        </div>
      </div>
    );
  }
}

function FormantForm(props) {
  return (
    <>
      <div>This is a not-accordion div</div>
      <Accordion>
        {
          elicitations.map((e, i) => {
            <FormantInput key={e} indexNumber={i} elicitation={e} formOnChange={(e, f, v) => props.changeHandler(e, f, v)}/>
          })
        }
      </Accordion>
    </>
  );
}

function FormantInput(props) {
  return (
    <>
      <div>And this is a not-accordion div inside the accordion bit</div>
      <Accordion.Item eventKey={props.indexNumber}>
        <Accordion.Header>"{props.elicitation}" vowel</Accordion.Header>
        <Accordion.Body>
          <label>
            F1: <input name="f1Input" type="number" onChange={e => props.formOnChange(props.elicitation, "f1", e.target.value)} />
          </label>
          <br/>
          <label>
            F2: <input name="f2Input" type="number" onChange={e => props.formOnChange(props.elicitation, "f2", e.target.value)}/>
          </label>
          <br/>
        </Accordion.Body>
      </Accordion.Item>
    </>
  );
}

export default App;
