import React from 'react';
import { Group } from '@visx/group';
import { Circle } from '@visx/shape';
import { scaleLinear, scaleOrdinal, scaleQuantize } from '@visx/scale';
import { AxisRight, AxisTop } from '@visx/axis';
import { GridRows, GridColumns } from '@visx/grid';
import { schemeRdYlBu, interpolateRdYlBu } from 'd3-scale-chromatic';
import { useTooltip, withTooltip, Tooltip, defaultStyles } from '@visx/tooltip';
import { WithTooltipProvidedProps } from '@visx/tooltip/lib/enhancers/withTooltip';

// tooltip stuff
const tooltipStyles = {
  ...defaultStyles,
  minWidth: 60,
  backgroundColor: 'rgba(180,180,180,0.75)',
  color: 'rgba(30,30,30,1)',
  fontSize: '0.8rem',
};
let tooltipTimeout;

const blue = "#1f78b4"
const orange = "#ff7f00"

// Finally we'll embed it all in an SVG
function ScatterPlot(props) {
  // Define the graph dimensions and margins
  const width = 680;
  const height = 630;
  const margin = { top: 40, bottom: 30, left: 40, right: 100 };
  // Then we'll create some bounds
  const xMax = width - margin.left - margin.right;
  const yMax = height - margin.top - margin.bottom;
  // We'll make some helpers to get at the data we want
  const x = d => parseFloat(d.f2);
  const y = d => parseFloat(d.f1);
  const c = d => d.dataset;
  // And then scale the graph by our data
  // TODO: update with Bark scale https://en.wikipedia.org/wiki/Bark_scale
  const xScale = scaleLinear({
    range: [xMax, 0],
    round: true,
    domain: [600, 2400], // Math.max(...data.map(x))
  });
  const yScale = scaleLinear({
    range: [0, yMax],
    round: true,
    domain: [250, 750], // Math.max(...data.map(y))] // change this to an API fetch for max PPH
  });
  const colourScale = scaleOrdinal({
    domain: ["reference", "user input"],
    range: [blue, orange],
  });
  // Compose together the scale and accessor functions to get point functions
  const compose = (scale, accessor) => data => scale(accessor(data));
  const xPoint = compose(xScale, x);
  const yPoint = compose(yScale, y);
  const categoryColour = compose(colourScale, c);
  // set up tooltips
  const {
    showTooltip,
    hideTooltip,
    tooltipOpen,
    tooltipData,
    tooltipLeft = 0,
    tooltipTop = 0,
  } = useTooltip({
    // initial tooltip state
    tooltipOpen: false,
    tooltipLeft: width / 3,
    tooltipTop: height / 3,
    tooltipData: 'Move me with your mouse or finger',
  });
  // render
  return (
      <div>
        <svg width={width} height={height} className="scatter-plot-chart">
          <Group left={margin.left} top={margin.top}>
            <GridRows scale={yScale} width={xMax} height={yMax} stroke="#e0e0e0" />
            <GridColumns scale={xScale} width={xMax} height={yMax} stroke="#e0e0e0" />
            <AxisTop label={"F2 (Hz)"} bottom={yMax} scale={xScale} numTicks={width > 461 ? 12 : 6} />
            <AxisRight label={"F1 (Hz)"} left={xMax} scale={yScale} numTicks={height > 491 ? 12 : 8}/>
            {props.data.map((d, i) => {
              return (
                <Group key={`circle-${i}`}>
                  <Circle
                    cx={xPoint(d)}
                    cy={yPoint(d)}
                    r={6}
                    fill={categoryColour(d)}
                    onMouseLeave={() => {
                      tooltipTimeout = window.setTimeout(() => {
                        hideTooltip();
                      }, 300);
                    }}
                    onMouseMove={() => {
                      if (tooltipTimeout) clearTimeout(tooltipTimeout);
                      showTooltip({
                        tooltipData: d,
                        tooltipTop: yPoint(d) + 30,
                        tooltipLeft: xPoint(d),
                      });
                    }}
                  />
                </Group>
              );
            })}
          </Group>
        </svg>

        {tooltipOpen && tooltipData && (
          <Tooltip top={tooltipTop} left={tooltipLeft} style={tooltipStyles}>
            <div className="tooltip-paragraph">
              {tooltipData.elicitation} vowel<br/>
              F1: {tooltipData.f1} Hz<br/>
              F2: {tooltipData.f2} Hz
            </div>
          </Tooltip>
        )}
      </div>
  );
}

export default ScatterPlot;
