          <NormalisationSelector selectedValue={normalisationMethod} changeHandler={handleNormalisationSelection}/>
          <DatasetSelector selectedValue={referenceDataset} changeHandler={handleReferenceDataSelection}/>
          <h5>Your data</h5>
          <FormantForm changeHandler={handleChange}/>
