import React from 'react';
import './App.css';
import ScatterPlot from './Plot.js';

const chartData = [
  {"elicitation" : "heed", "f1" : 310, "f2" : 1933},
  {"elicitation" : "hard", "f1" : 667, "f2" : 1056},
  {"elicitation" : "hood", "f1" : 444, "f2" : 880},
]

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedSite: null,
      hoveredNode: null,
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(ID) {
    this.setState({selectedSite: ID});
  }

  render() {
    return (
      <div className="App">
        <div id="visualisation-pane">
          <div id="visualisation-pane-content">
            <ScatterPlot data={chartData} />
          </div>
        </div>
        <div>
          This is not a visualisation pane.
        </div>
      </div>
    );
  }
}

export default App;
