import React from 'react';
import Accordion from 'react-bootstrap/Accordion';
import Dropdown from 'react-bootstrap/Dropdown';
import ScatterPlot from './Plot.js';

import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const chartData = {
  "Australian English 1991-1994" : [
    {"elicitation" : "heed", "f1" : 320, "f2" : 2360, "description" : "iː", "dataset" : "reference"},
    {"elicitation" : "hid", "f1" : 330, "f2" : 2350, "description" : "ɪ", "dataset" : "reference"},
    {"elicitation" : "hair", "f1" : 450, "f2" : 2100, "description" : "eː", "dataset" : "reference"},
    {"elicitation" : "head", "f1" : 470, "f2" : 2090, "description" : "e", "dataset" : "reference"},
    {"elicitation" : "had", "f1" : 690, "f2" : 1760, "description" : "æ", "dataset" : "reference"},
    {"elicitation" : "hard", "f1" : 760, "f2" : 1350, "description" : "ɐː", "dataset" : "reference"},
    {"elicitation" : "hud", "f1" : 740, "f2" : 1390, "description" : "ɐ", "dataset" : "reference"},
    {"elicitation" : "hod", "f1" : 580, "f2" : 1040, "description" : "ɔ", "dataset" : "reference"},
    {"elicitation" : "horde", "f1" : 440, "f2" : 850, "description" : "oː", "dataset" : "reference"},
    {"elicitation" : "hood", "f1" : 380, "f2" : 950, "description" : "ʊ", "dataset" : "reference"},
    {"elicitation" : "who'd", "f1" : 340, "f2" : 1800, "description" : "ʉ", "dataset" : "reference"},
    {"elicitation" : "herd", "f1" : 470, "f2" : 1640, "description" : "ɜː", "dataset" : "reference"}
  ]
}

// const diphthongs = {
//   "Australian English 1991-1994" : [
//     {"elicitation" : "hoyed", "f1" : , "f2" : , "description" : "oɪ", "dataset" : "reference"},
//     {"elicitation" : "hide", "f1" : , "f2" : , "description" : "ɑɪ", "dataset" : "reference"},
//     {"elicitation" : "hayed", "f1" : , "f2" : , "description" : "æɪ", "dataset" : "reference"},
//     {"elicitation" : "hoed", "f1" : , "f2" : , "description" : "əʉ", "dataset" : "reference"},
//     {"elicitation" : "how'd", "f1" : , "f2" : , "description" : "æɔ", "dataset" : "reference"}
//   ]
// }

const elicitations = [
  "heed", "hid", "head", "haired", "had", "who'd", "heard", "hud", "hard",
  "hood", "hoard"
]

const diphthongElicitations = [
  "hoyed", "hide", "hayed", "hoed", "how'd"
]

function reshapeUserData(userFormantValues) {
  let userChartData = [];
  for (let elicitation in userFormantValues) {
    if (userFormantValues[elicitation]["f1"] && userFormantValues[elicitation]["f2"]) {
      userChartData.push({
        "elicitation" : elicitation,
        "f1" : parseFloat(userFormantValues[elicitation]["f1"]),
        "f2" : parseFloat(userFormantValues[elicitation]["f2"]),
        "dataset" : "user input"
      });
    }
  }
  return userChartData
}

// define maths components
const e = Math.exp(1)

function antilog(n, base = e) {
  if (base === e) return Math.exp(n);
  return Math.pow(base, n);
}

function average(inputArray) {
  return inputArray.reduce((a, b) => a + b, 0) / inputArray.length;
}

function normaliseNearey1(formant, allFormants) {
  return antilog(log(formant)) - average(allFormants);
}

function normaliseDataset(dataset) {
  const f1s = dataset.map(row => row["f1"]);
  const f2s = dataset.map(row => row["f2"]);
  for (let i = 0; i < dataset.length; i++) {
    dataset[i]["normalisedF1"] = normaliseNearey1(dataset[i]["f1"], f1s);
    dataset[i]["normalisedF1"] = normaliseNearey1(dataset[i]["f2"], f2s);
  }
  return dataset;
}

class App extends React.Component {
  constructor(props) {
    super(props);
    // TODO: generate for all words in const array elicitations
    this.state = {
      formantValues: elicitations.reduce((o, key) => ({ ...o, [key]: {"f1" : "", "f2" : ""}}), {}),
      selectedReferenceDataset: "Australian English 1991-1994"
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(elicitation, formant, value) {
    // update formant values
    let updateObj = this.state.formantValues;
    updateObj[elicitation][formant] = value;
    this.setState({formantValues : updateObj});
    // add any
  }

  handleReferenceDataSelection(referenceDataset) {
    let updateObj = this.state.formantValues;
    updateObj["selectedReferenceDataset"] = referenceDataset;
    this.setState(updateObj);
  }

  render() {
    return (
      <div className="App">
        <div id="visualisation-pane">
          <div id="visualisation-pane-content">
            <ScatterPlot data={normaliseDataset(chartData[this.state.selectedReferenceDataset]).concat(normaliseDataset(reshapeUserData(this.state.formantValues)))} />
          </div>
        </div>
        <div className="formant-form-container">
          <DatasetSelector DatasetSelectionChange={e => this.handleReferenceDataSelection(e)}/>
          <FormantForm changeHandler={(e, f, v) => this.handleChange(e, f, v)}/>
        </div>
      </div>
    );
  }
}

function DatasetSelector(props) {
  return (
    <Dropdown className="dataset-selector" onSelect={e => props.DatasetSelectionChange(e)}>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        Reference dataset
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item eventKey="Australian English 1991-1994">Australian English 1991-1994</Dropdown.Item>
        <Dropdown.Item eventKey="Australian English 196?">Australian English 196?</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
  );
}

function FormantForm(props) {
  return (
    <Accordion className="formant-form">
      {
        elicitations.map((e, i) => <FormantInput key={e} indexNumber={i} elicitation={e} formOnChange={(e, f, v) => props.changeHandler(e, f, v)}/>)
      }
    </Accordion>
  );
}

function FormantInput(props) {
  return (
    <Accordion.Item eventKey={props.indexNumber}>
      <Accordion.Header>"{props.elicitation}" vowel</Accordion.Header>
      <Accordion.Body>
        <label>
          F1: <input name="f1Input" type="number" onChange={e => props.formOnChange(props.elicitation, "f1", e.target.value)} />
        </label>
        <br/>
        <label>
          F2: <input name="f2Input" type="number" onChange={e => props.formOnChange(props.elicitation, "f2", e.target.value)}/>
        </label>
        <br/>
      </Accordion.Body>
    </Accordion.Item>
  );
}

export default App;
