  const [formantValues, setFormantValues] = useState(
    elicitations.reduce((o, key) => ({ ...o, [key]: {"f1" : "", "f2" : ""}}), {})
  );
  const [referenceDataset, setReferenceDataset] = useState("Australian English 1991-1994");
