      </div>
      <div className="formant-form-container">
        <NormalisationSelector changeHandler={handleNormalisationSelection}/>
        <DatasetSelector changeHandler={handleReferenceDataSelection}/>
        <FormantForm changeHandler={handleChange}/>
      </div>
    </div>
  );
}

function NormalisationSelector({ changeHandler }) {
  return (
    <Dropdown className="dataset-selector" onSelect={changeHandler}>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        Normalisation method
      </Dropdown.Toggle>
      <Dropdown.Menu>
        <Dropdown.Item eventKey="none">none</Dropdown.Item>
        <Dropdown.Item eventKey="Nearey 1">Nearey 1</Dropdown.Item>
        <Dropdown.Item eventKey="Lobanov">Lobanov</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
  );
}

function DatasetSelector({ changeHandler }) {
  return (
    <Dropdown className="dataset-selector" onSelect={changeHandler}>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        Reference dataset
      </Dropdown.Toggle>
      <Dropdown.Menu>
        <Dropdown.Item eventKey="Australian English 1991-1994">Australian English 1991-1994</Dropdown.Item>
        <Dropdown.Item eventKey="Australian English 196?">Australian English 196?</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
  );
}

function FormantForm(props) {
  return (
    <Accordion className="formant-form">
      {
        elicitations.map((e, i) => <FormantInput key={e} indexNumber={i} elicitation={e} formOnChange={(e, f, v) => props.changeHandler(e, f, v)}/>)
      }
    </Accordion>
  );
}

function FormantInput(props) {
  return (
    <Accordion.Item eventKey={props.indexNumber}>
      <Accordion.Header>"{props.elicitation}" vowel</Accordion.Header>
      <Accordion.Body>
        <label>
          F1: <input name="f1Input" type="number" onChange={e => props.formOnChange(props.elicitation, "f1", e.target.value)} />
        </label>
        <br/>
        <label>
          F2: <input name="f2Input" type="number" onChange={e => props.formOnChange(props.elicitation, "f2", e.target.value)}/>
        </label>
        <br/>
      </Accordion.Body>
    </Accordion.Item>
  );
}

export default App;
